import React from "react";
import "./BlogCard.css";
import { Fade } from "react-reveal";
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';


export default function BlogCard({ blog }) {
  function openUrlInNewTab(url) {
    if (url !== undefined) {
      var win = window.open(url, "_blank");
      win.focus();
    }
  }

  const GetBullets = ({ bullets }) => {
    return bullets ? bullets.map((item) => <li className="subTitle">{item}</li>) : null
  };

  return (
    <Fade bottom duration={1000} distance="20px">
      <div>
        <div
          className="blog-container"
          onClick={() => openUrlInNewTab(blog.url)}
        >
          <a className="blog-card" href="#blog">
          <div className="blog-carousel-container">
          <Carousel autoPlay infiniteLoop interval={5000} showStatus={false} showIndicators={false} showThumbs={false}>
              {/* Assuming blog.images is an array of image URLs */}
              {blog.images.map((image, index) => (
                <div key={index}>
                  <img src={image} alt={``} style={{ width: 350 }}/>
                </div>
              ))}
            </Carousel>
            </div>
            <div className="blog-details"> {/* Wrap title and description in a div */}
