import React from "react";
import "./BlogCard.css";
import { Fade } from "react-reveal";
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';


export default function BlogCard({ blog }) {
  function openUrlInNewTab(url) {
    if (url !== undefined) {
      var win = window.open(url, "_blank");
      win.focus();
    }
  }

  return (
    <Fade bottom duration={1000} distance="20px">
      <div>
        <div
          className="blog-container"
          onClick={() => openUrlInNewTab(blog.url)}
        >
          <a className="blog-card" href="#blog">
          <div className="blog-carousel-container">
          <Carousel autoPlay infiniteLoop interval={3000} showStatus={false} showIndicators={true} showThumbs={false}>
              {/* Assuming blog.images is an array of image URLs */}
              {blog.images.map((image, index) => (
                <div key={index}>
                  <img src={image} alt={``} style={{ width: '0%', height: '50%' }}/>
                </div>
              ))}
            </Carousel>
            </div>
            <div className="blog-details"> {/* Wrap title and description in a div */}
              <h3 className="blog-title">{blog.title}</h3>
              <p className="small">{blog.description}</p>
            </div>
            <div className="go-corner">
              <div className="go-arrow">â†’</div>
            </div>
          </a>
        </div>
      </div>
    </Fade>
  );
}
