import React from "react";
import "./ListOfSkills.css";
import { skillsSection } from "../../portfolio";

const SkillLegend = () => {
  return (
    <div className="legend">
      <div className="legend-item">
        <div className="circle advanced"></div>
        <span>Advanced</span>
      </div>
      <div className="legend-item">
        <div className="circle intermediate"></div>
        <span>Intermediate</span>
      </div>
      <div className="legend-item">
        <div className="circle basic"></div>
        <span>Basic</span>
      </div>
    </div>
  );
};

const SkillCircles = ({ skill, level }) => {
  const renderCircles = () => {
    const circles = [];
    let advancedCount = 0;
    let intermediateCount = 0;

    for (let i = 0; i < level; i++) {
      if (advancedCount < 2) {
        circles.push(<div key={i} className="circle advanced"></div>);
        advancedCount++;
      } else if (intermediateCount < 2) {
        circles.push(<div key={i} className="circle intermediate"></div>);
        intermediateCount++;
      } else {
        circles.push(<div key={i} className="circle basic"></div>);
      }
    }
    return circles;
  };

  return (
    <div className="skill">
      <span className="skill-name">{skill}</span>
      <div className="circle-container">{renderCircles()}</div>
    </div>
  );
};


export default function ListOfSkills() {
  return (
    <div className="app">
    <h1>Skills</h1>
    <SkillLegend />
    <br /> {/* Add a break here */}
    <div className="skill-grid">
      {skillsSection.listSkills.map((skill, index) => (
        <SkillCircles key={index} skill={skill.name} level={skill.level} />
      ))}
    </div>
  </div>


  );
}
