import React from "react";
import "./WorkExperience.css";
import ExperienceCard from "../../components/experienceCard/ExperienceCard";
import { workExperiences } from "../../portfolio";
import { Fade } from "react-reveal";



export default function WorkExperience({ workExperiences }) {
    if (workExperiences.viewExperiences) {
        return (
            <div id="experience">
                <Fade bottom duration={1000} distance="20px">
                    <div className="experience-container" id="workExperience">
                        <div>
                            <h1 className="experience-heading">Experiences</h1>
                            <div className="experience-cards-div">
                                {/* Divide experience cards into groups of 2 */}
                                {workExperiences.experience.reduce((groups, card, index) => {
                                    if (index % 2 === 0) groups.push([]);
                                    groups[groups.length - 1].push(card);
                                    return groups;
                                }, []).map((group, groupIndex) => (
                                    <div key={groupIndex} className="experience-card-row">
                                        {group.map((card, index) => (
                                            <ExperienceCard
                                                key={index}
                                                cardInfo={{
                                                    company: card.company,
                                                    desc: card.desc,
                                                    date: card.date,
                                                    companylogo: card.companylogo,
                                                    role: card.role,
                                                    descBullets: card.descBullets
                                                }}
                                            />
                                        ))}
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </Fade>
            </div>
        );
    }
    return null;
}
